// src/App.js
import React from 'react';
import { BrowserRouter as Router, Route, Routes, Navigate } from 'react-router-dom';
import CreatePaperWithUser from './components/CreatePaperWithUser';
import ViewUsers from './components/ViewUsers';
import ViewPapers from './components/ViewPapers';
import SignIn from './components/SignInPage';
import UserPapersPage from './components/UserPapersPage';
import { AuthProvider } from './context/AuthContext';
import ProtectedRoute from './components/ProtectedRoute';
import { UserProvider } from './context/UserContext';
import { AdminAuthProvider } from './context/AdminAuthContext';
import { AdminProvider } from './context/AdminContext';
import Home from './components/Home';
import { useAdminAuth } from '../context/AdminAuthContext';

function App() {
  return (
    <>
      <AuthProvider>
        <UserProvider> {/* Wrap Routes in UserProvider to provide userId context */}
          <Router>
            <div className="min-h-screen bg-gray-100 flex flex-col justify-center items-center">
              <Routes>
                <Route path="/signin" element={<SignIn />} />

                {/* Protected Routes */}
                <Route
                  path="/"
                  element={
                    <ProtectedRoute>
                      <Home />
                    </ProtectedRoute>
                  }
                />
                <Route
                  path="/create/paper"
                  element={
                    <ProtectedRoute>
                      <CreatePaperWithUser />
                    </ProtectedRoute>
                  }
                />
                <Route
                  path="/user/papers"
                  element={
                    <ProtectedRoute>
                      <UserPapersPage />
                    </ProtectedRoute>
                  }
                />

                {/* Catch-all route for undefined paths */}
                <Route path="*" element={<Navigate to="/" replace />} />
              </Routes>
            </div>
          </Router>
        </UserProvider>
      </AuthProvider>
      <AdminAuthProvider>
        <AdminProvider>
          <Router>
            <Routes>
              <Route
                path="/users"
                element={
                  <ProtectedRoute>
                    <ViewUsers />
                  </ProtectedRoute>
                }
              />
              <Route
                path="/papers"
                element={
                  <ProtectedRoute>
                    <ViewPapers />
                  </ProtectedRoute>
                }
              />
              <Route path="*" element={<Navigate to="/" replace />} />
            </Routes>
          </Router>
        </AdminProvider>
      </AdminAuthProvider>
    </>
  );
}

export default App;
