import React, { useState } from 'react';
import { GoogleOAuthProvider, GoogleLogin } from '@react-oauth/google';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';

function SignInPage() {
  const navigate = useNavigate();
  const [errorMessage, setErrorMessage] = useState(null);

  const handleGoogleSignIn = (googleUser) => {
    const email = googleUser.getBasicProfile().getEmail(); // For Google OAuth
    console.log("Logged in email:", email);
    handleLoginSuccess({ email });  // Pass email to the login success handler
  };


  const handleLoginSuccess = (response) => {
    const email = response.email;  // Ensure email is retrieved correctly
    console.log("Email being passed to server:", email);  // Log the email to verify

    if (!email) {
        alert("Email is undefined, cannot proceed with login");
        return;
    }

    axios.get(`/login/user/${email}`)
        .then((res) => {
            if (res.data.authorize === 'YES') {
                // Allow access to the user
            } else {
                alert('Access denied');
            }
        })
        .catch((err) => {
            console.error("Error during email check:", err);
            alert("An error occurred while checking your email.");
        });
};

  

  // Handle the error response from Google login
  const handleLoginError = () => {
    console.log('Google Login Failed');
  };

  const handleLoginFailure = () => {
    setErrorMessage("Login failed, please try again.");
  };

  return (
    <div className="min-h-screen bg-gradient-to-r from-blue-500 to-teal-500 flex justify-center items-center">
      <div className="bg-white p-6 rounded-lg shadow-lg w-96">
        <h2 className="text-2xl font-semibold text-center mb-6">Sign In</h2>
        
        {errorMessage && (
          <p className="text-red-500 text-center mb-4">{errorMessage}</p>
        )}

        <GoogleOAuthProvider clientId="526314418933-ai3uvq89emkiod12khrhn040vittie77.apps.googleusercontent.com">
          <GoogleLogin 
            onSuccess={handleLoginSuccess} 
            onError={handleLoginFailure} 
            useOneTap
          />
        </GoogleOAuthProvider>
      </div>
    </div>
  );
}

export default SignInPage;
