// src/App.js
import React from 'react';
import { BrowserRouter as Router, Route, Routes, Navigate } from 'react-router-dom'; // Add Navigate import here
import CreatePaperWithUser from './components/CreatePaperWithUser';
import ViewUsers from './components/ViewUsers';
import ViewPapers from './components/ViewPapers';
import SignIn from './components/SignInPage';
import UserPapers from './components/UserPapers';
import { AuthProvider } from './context/AuthContext';
import ProtectedRoute from './components/ProtectedRoute';
import UserPapersPage from './components/UserPapersPage';
import { UserProvider } from './contexts/UserContext';

function App() {
  return (
    <AuthProvider>
      <Router>
        <div className="min-h-screen bg-gray-100 flex flex-col justify-center items-center">
          <Routes>
            <Route path="/signin" element={<SignIn />} />

            {/* Protected Routes */}
            <Route
              path="/create/paper/:id"
              element={
                <ProtectedRoute>
                  <CreatePaperWithUser />
                </ProtectedRoute>
              }
            />
            <Route
              path="/users/:id"
              element={
                <ProtectedRoute>
                  <ViewUsers />
                </ProtectedRoute>
              }
            />
            <Route
              path="/papers/:id"
              element={
                <ProtectedRoute>
                  <ViewPapers />
                </ProtectedRoute>
              }
            />
            <Route
              path="/user/papers/:id"
              element={
                <ProtectedRoute>
                  <UserPapersPage />
                </ProtectedRoute>
              }
            />

            {/* Catch-all route for undefined paths */}
            <Route path="*" element={<Navigate to="/users" replace />} />
          </Routes>
        </div>
      </Router>
    </AuthProvider>
  );
}

export default App;
