import React, { useState } from 'react';
import axios from 'axios';

const CreatePaper = ({ userId }) => {
  const [paperDetails, setPaperDetails] = useState({
    title: '',
    author: '',
    DOI: '',
    publisher: '',
    year: '',
    journal: ''
  });
  const [file, setFile] = useState(null);
  const [message, setMessage] = useState('');

  // Handle input changes for text fields
  const handleChange = (e) => {
    const { name, value } = e.target;
    setPaperDetails({
      ...paperDetails,
      [name]: value
    });
  };

  // Handle file selection
  const handleFileChange = (e) => {
    setFile(e.target.files[0]);
  };

  // Submit paper details via form
  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post(`http://localhost:3000/${userId}/paper`, paperDetails);
      setMessage('Paper created successfully!');
      setPaperDetails({
        title: '',
        author: '',
        DOI: '',
        publisher: '',
        year: '',
        journal: ''
      });
    } catch (error) {
      setMessage('Error creating paper: ' + error.response.data.message);
    }
  };

  // Submit paper details via file upload
  const handleFileSubmit = async (e) => {
    e.preventDefault();
    if (!file) {
      setMessage('Please select a file to upload.');
      return;
    }

    const formData = new FormData();
    formData.append('file', file);

    try {
      const response = await axios.post('http://localhost:3000/upload', formData, {
        headers: { 'Content-Type': 'multipart/form-data' }
      });

      // Assuming the server responds with parsed data in JSON
      const uploadedPaper = response.data;

      // Update the form fields with the extracted data from file
      setPaperDetails({
        title: uploadedPaper.title || '',
        author: uploadedPaper.author || '',
        DOI: uploadedPaper.DOI || '',
        publisher: uploadedPaper.publisher || '',
        year: uploadedPaper.year || '',
        journal: uploadedPaper.journal || ''
      });
      setMessage('File processed successfully! Review and submit paper.');

    } catch (error) {
      setMessage('Error processing file: ' + error.message);
    }
  };

  return (
    <div>
      <h2>Create Paper</h2>

      {/* Paper Details Form */}
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          name="title"
          placeholder="Title"
          value={paperDetails.title}
          onChange={handleChange}
          required
        />
        <input
          type="text"
          name="author"
          placeholder="Author"
          value={paperDetails.author}
          onChange={handleChange}
          required
        />
        <input
          type="text"
          name="DOI"
          placeholder="DOI"
          value={paperDetails.DOI}
          onChange={handleChange}
          required
        />
        <input
          type="text"
          name="publisher"
          placeholder="Publisher"
          value={paperDetails.publisher}
          onChange={handleChange}
        />
        <input
          type="number"
          name="year"
          placeholder="Year"
          value={paperDetails.year}
          onChange={handleChange}
          required
        />
        <input
          type="text"
          name="journal"
          placeholder="Journal"
          value={paperDetails.journal}
          onChange={handleChange}
        />
        <button type="submit">Create Paper</button>
      </form>

      <hr />

      {/* File Upload Section */}
      <h3>Or Upload a File to Create Paper</h3>
      <form onSubmit={handleFileSubmit}>
        <input type="file" onChange={handleFileChange} required />
        <button type="submit">Upload File</button>
      </form>

      {message && <p>{message}</p>}
    </div>
  );
};

export default CreatePaper;
