import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import 'bootstrap/dist/css/bootstrap.min.css';

const Home = () => {
  const userId = localStorage.getItem('id');
  const [user, setUser] = useState([]);
  const dp = localStorage.getItem('DP');
  const navigate = useNavigate();

  const navPapers = () => {
    navigate('/user/papers');
  };

  const navCreatePaper = () => {
    navigate('/create/paper');
  };

  const navTags = () => {
    navigate('/user/taggedpapers');
  };

  useEffect(() => {
    axios
      .get(`http://localhost:3000/user/byid/${userId}`)
      .then((response) => {
        setUser(response.data); // Set the fetched user to state
      })
      .catch((error) => {
        console.error('Error fetching user:', error);
      });
  }, [userId]);

  console.log(user);

  return (
    <div className="container-fluid bg-gray-100 min-vh-100">
      {/* Bootstrap Navbar */}
      <nav className="navbar navbar-expand-lg navbar-dark bg-dark">
        <div className="container">
          <a className="navbar-brand" href="/">
            My App
          </a>
          <button
            className="navbar-toggler"
            type="button"
            data-bs-toggle="collapse"
            data-bs-target="#navbarNav"
            aria-controls="navbarNav"
            aria-expanded="false"
            aria-label="Toggle navigation"
          >
            <span className="navbar-toggler-icon"></span>
          </button>
          <div className="collapse navbar-collapse" id="navbarNav">
            <ul className="navbar-nav">
              <li className="nav-item">
                <button
                  className="btn btn-link nav-link"
                  onClick={navPapers}
                >
                  View Your Citations
                </button>
              </li>
              <li className="nav-item">
                <button
                  className="btn btn-link nav-link"
                  onClick={navTags}
                >
                  View Your Tagged Citations
                </button>
              </li>
              <li className="nav-item">
                <button
                  className="btn btn-link nav-link"
                  onClick={navCreatePaper}
                >
                  Upload New Citation
                </button>
              </li>
            </ul>
          </div>
        </div>
      </nav>

      {/* Main Content */}
      <div className="container mt-5">
        <div className="bg-white p-6 rounded-lg shadow-lg">
          <h2 className="text-3xl font-bold text-center mb-6">My Profile</h2>
          <div className="row">
            {/* Left Column */}
            <div className="col-md-6">
              <div className="mb-3">
                <label className="form-label">Full Name:</label>
                <input
                  type="text"
                  className="form-control"
                  placeholder={user.name}
                />
              </div>
              <div className="mb-3">
                <label className="form-label">PSR:</label>
                <input
                  type="text"
                  className="form-control"
                  placeholder={user.PSR}
                />
              </div>
              <div className="mb-3">
                <label className="form-label">Department:</label>
                <input
                  type="text"
                  className="form-control"
                  placeholder={user.Dept}
                />
              </div>
              <div className="mb-3">
                <label className="form-label">Email:</label>
                <input
                  type="email"
                  className="form-control"
                  placeholder={user.email}
                />
              </div>
              <div className="mb-3">
                <label className="form-label">Phone:</label>
                <input
                  type="text"
                  className="form-control"
                  placeholder={user.PhoneNum}
                />
              </div>
              <div className="mb-3">
                <label className="form-label">Chamber Number:</label>
                <input
                  type="text"
                  className="form-control"
                  placeholder={user.chamberNum}
                />
              </div>
            </div>

            {/* Right Column */}
            <div className="col-md-6 text-center">
              <img
                src={dp || 'default-avatar.png'}
                alt="Profile"
                className="img-thumbnail rounded-circle mb-3"
                style={{ width: '150px', height: '150px' }}
              />
              <div className="mb-3">
                <label className="form-label">DOB:</label>
                <input
                  type="text"
                  className="form-control"
                  placeholder={user.DOB}
                />
              </div>
            </div>
          </div>
          <div className="text-end">
            <button className="btn btn-success">Save</button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Home;
