import React, { useState } from 'react';
import {
  MDBIcon,
  MDBBtn,
  MDBSideNav,
  MDBSideNavItem,
  MDBSideNavLink,
  MDBSideNavMenu,
  MDBSideNavCollapse,
} from 'mdb-react-ui-kit';
import { useNavigate } from 'react-router-dom';
import 'mdb-react-ui-kit/dist/css/mdb.min.css';


export default function App() {
  const [isSidebarOpen, setSidebarOpen] = useState(true);
  const [citationsCollapse, setCitationsCollapse] = useState(false);
  const [tagsCollapse, setTagsCollapse] = useState(false);
  const navigate = useNavigate();

  const navTo = (path) => {
    navigate(path);
    setSidebarOpen(false); // Optionally close the sidebar on navigation
  };

  return (
    <>
      <MDBSideNav
        isOpen={isSidebarOpen}
        absolute
        getOpenState={(e) => setSidebarOpen(e)}
        className="bg-dark text-white"
      >
        <MDBSideNavMenu>
          <MDBSideNavItem>
            <MDBSideNavLink onClick={() => navTo('/user/papers')}>
              <MDBIcon fas icon="file-alt" className="fa-fw me-3" />
              <span>View Your Citations</span>
            </MDBSideNavLink>
          </MDBSideNavItem>

          <MDBSideNavItem>
            <MDBSideNavLink
              icon="angle-down"
              shouldBeExpanded={tagsCollapse}
              onClick={() => setTagsCollapse(!tagsCollapse)}
            >
              <MDBIcon fas icon="tags" className="fa-fw me-3" />
              <span>View Tagged Citations</span>
            </MDBSideNavLink>
            <MDBSideNavCollapse show={tagsCollapse}>
              <MDBSideNavLink onClick={() => navTo('/user/taggedpapers')}>
                Tagged Citations
              </MDBSideNavLink>
            </MDBSideNavCollapse>
          </MDBSideNavItem>

          <MDBSideNavItem>
            <MDBSideNavLink onClick={() => navTo('/create/paper')}>
              <MDBIcon fas icon="upload" className="fa-fw me-3" />
              <span>Upload New Citation</span>
            </MDBSideNavLink>
          </MDBSideNavItem>
        </MDBSideNavMenu>
      </MDBSideNav>

      <div style={{ padding: '20px' }} className="text-center">
        <MDBBtn onClick={() => setSidebarOpen(!isSidebarOpen)}>
          <MDBIcon fas icon="bars" />
        </MDBBtn>
      </div>
    </>
  );
}
