// src/components/UserPapersPage.js

import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useParams } from "react-router-dom";
import { useUser } from '../context/UserContext';

const UserPapersPage = () => {
  const [papers, setPapers] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const { userId } = useUser();

  // Debugging userId
  console.log("User ID:", userId);

  useEffect(() => {
    if (!userId) return; // Skip if userId is not available yet

    const fetchPapers = async () => {
      try {
        console.log("Fetching papers for user:", userId);
        const response = await axios.get(`http://localhost:3000/user/${userId}/papers`);
        console.log("API Response:", response.data);

        if (Array.isArray(response.data)) {
          setPapers(response.data);
        } else {
          setError("Invalid data format");
        }
      } catch (err) {
        setError("Error fetching papers");
        console.error(err);
      } finally {
        setLoading(false);
      }
    };

    fetchPapers();
  }, [userId]);

  if (loading) return <p>Loading papers...</p>;
  if (error) return <p>{error}</p>;

  return (
    <div className="p-4">
      <h1 className="text-3xl font-semibold mb-6">Your Published Papers</h1>
      {papers.length > 0 ? (
        <div className="space-y-4">
          {papers.map((paper) => (
            <div key={paper._id} className="p-4 bg-gray-100 rounded-lg shadow-md">
              <h2 className="text-xl font-bold">{paper.title}</h2>
              <p>Author(s): {paper.author}</p>
              <p>DOI: {paper.DOI}</p>
              <p>Publisher: {paper.publisher}</p>
              <p>Year: {paper.year}</p>
              <p>Journal: {paper.journal}</p>
              <p>Volume: {paper.volume}</p>
              <p>Pages: {paper.pages}</p>
            </div>
          ))}
        </div>
      ) : (
        <p>No papers published yet.</p>
      )}
    </div>
  );
};

export default UserPapersPage;
