import React, { useState, useEffect } from 'react';
import {
    createUser, getUserByEmail, deleteUser, getAllUsers,
    createPaper, getPaperById, getAllPapers,
    uploadFile, deletePaperByDOI,
    deletePaperById
} from './api';

function App() {
    const [users, setUsers] = useState([]);
    const [papers, setPapers] = useState([]);
    const [file, setFile] = useState(null);
    const [email, setEmail] = useState('');
    const [userName, setUserName] = useState('');
    const [DOI, setDOI] = useState('');

    // Fetch all users
    useEffect(() => {
        getAllUsers().then(response => setUsers(response.data)).catch(console.error);
        getAllPapers().then(response => setPapers(response.data)).catch(console.error);
    }, []);

    // Create a new user
    const handleCreateUser = () => {
        createUser({ email, name: userName })
            .then(response => setUsers([...users, response.data]))
            .catch(console.error);
    };

    // Upload a file
    const handleFileUpload = (file, userId) => {
        uploadFile(file, userId)
        setTimeout(0);
    };

    // Create a paper
    const handleCreatePaper = (userId) => {
        const paperData = {
          author: "Vamsi Inturi and GR Sabareesh and K Supradeepan and PK Penumakala",
          title: "Integrated condition monitoring scheme for bearing fault diagnosis of a wind turbine gearbox",
          year: "2019",
          journal: "Journal of Vibration and Control",
          volume: "25",
          pages: "1852-1865",
          DOI: "10.1177/1077546319841495"
        };

        createPaper(userId, paperData)
            .then(response => setPapers([...papers, response.data]))
            .catch(console.error);
    };

    return (
        <div className="App">
            <h2>User Management</h2>
            <input placeholder="Name" value={userName} onChange={(e) => setUserName(e.target.value)} />
            <input placeholder="Email" value={email} onChange={(e) => setEmail(e.target.value)} />
            <button onClick={handleCreateUser}>Create User</button>

            <h2>Users</h2>
            <ul>
                {users.map(user => (
                    <li key={user._id}>
                        {user.name} ({user.email})
                        <button onClick={() => deleteUser(user._id).then(() => setUsers(users.filter(u => u._id !== user._id)))}>Delete</button>
                        <button onClick={() => handleCreatePaper(user._id)}>Create Paper</button>
                        <input type="file" onChange={(e) => setFile(e.target.files[0])} />
                        <button onClick={() => handleFileUpload(file, user._id)}>Upload</button>
                    </li>
                ))}
            </ul>

            <h2>Paper Management</h2>
            <input placeholder="DOI" value={DOI} onChange={(e) => setDOI(e.target.value)} />

            <h2>All Papers</h2>
            <ul>
                {papers.map(paper => (
                    <li key={paper._id}>
                        {paper.title} (DOI: {paper.DOI})
                        <button onClick={() => deletePaperById(paper._id).then(() => setPapers(papers.filter(u => u._id !== paper._id)))}>Delete</button>
                    </li>
                ))}
            </ul>

        </div>
    );
}

export default App;
