import React, { useState, useEffect } from 'react';
import { usePaper } from '../context/PaperContext';
import axios from 'axios';

const Tag = () => {
    const [userlist, setuserlist] = useState([]);
    const users = [];
    const [searchText, setSearchText] = useState('');
    const [selectedUsers, setSelectedUsers] = useState([]);
    useEffect(() => {
        axios.get('http://localhost:3000/alluser')
            .then(response => {
                console.log(response.data);
                setuserlist(response.data); // Set the fetched users to state
            })
            .catch(error => {
                console.error('Error fetching users:', error);
            });
    }, []);

    useEffect(() => {
        for(const user of userlist){
            users.push([user.name, user.email, user._id]);
        }
        console.log(users);
    }, [userlist])

    return (
    <div className="max-w-lg mx-auto p-6 bg-white shadow-md rounded-lg">
      <h1 className="text-2xl font-semibold mb-4">Tag Users</h1>

      {/* Search Bar */}
      <div className="relative mb-4">
      <input
          type="text"
          value={searchText}
          onChange={(e) => setSearchText(e.target.value)}
          placeholder="Search for users..."
          className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:outline-none focus:border-indigo-500"
        />
      </div>

      {/* Display Selected Users */}
      <div className="mb-4">
        <h2 className="text-lg font-medium mb-2">Tagged Users</h2>
        <p className="text-gray-600">No users tagged yet.</p>
      </div>

      {/* Save Button */}
      <button
        className="w-full bg-indigo-500 text-white py-2 rounded-lg mt-4 hover:bg-indigo-600 transition-colors"
      >
        Save Tags
      </button>
    </div>
  );
};

export default Tag;
