import React, { useState } from 'react';
import axios from 'axios';

const SignInPage = () => {
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');

    const handleEmailChange = (e) => {
        setEmail(e.target.value);  // Update email state with input value
    };

    const handlePasswordChange = (e) => {
        setPassword(e.target.value);  // Update password state with input value
    };

    const handleLoginSuccess = async () => {
        console.log("Email being passed to server:", email);  // Log the email before making the request
        if (!email) {
            alert("Email is undefined, cannot proceed with login");
            return;
        }

        try {
            const response = await axios.get(`http://localhost:3001/login/user/${email}`);
            if (response.data.authorize === 'YES') {
                console.log("User authorized, proceed");
                // Proceed with the next steps (e.g., redirect, set session, etc.)
            } else {
                alert('Access denied');
            }
        } catch (err) {
            console.error("Error during email check:", err);
            alert("An error occurred while checking your email.");
        }
    };

    return (
        <div>
            <input
                type="email"
                value={email}
                onChange={handleEmailChange}
                placeholder="Enter your email"
            />
            <input
                type="password"
                value={password}
                onChange={handlePasswordChange}
                placeholder="Enter your password"
            />
            <button onClick={handleLoginSuccess}>Sign In</button>
        </div>
    );
};

export default SignInPage;
