import React, { useState, useEffect } from 'react';
import { Navigate } from 'react-router-dom';
import { useAdminAuth } from '../context/AdminAuthContext';
import { useAuth } from '../context/AuthContext';

function AdminProtectedRoute({ children }) {
  const { isAuthenticatedAdmin } = useAdminAuth();
  const { isAuthenticated } = useAuth();
  const [restrictedMessage, setRestrictedMessage] = useState(false);

  useEffect(() => {
    // Show restricted message if the user is authenticated but not as an admin
    if (isAuthenticated && !isAuthenticatedAdmin) {
      setRestrictedMessage(true);
    }
  }, [isAuthenticated, isAuthenticatedAdmin]);

  // If an admin is authenticated, render the children (admin page)
  if (isAuthenticatedAdmin) {
    return children;
  }

  // If a regular user tries to access, display the restricted message
  if (restrictedMessage) {
    return (
      <div className="text-red-600 text-center mt-4">
        Access Restricted: You do not have permission to view this page.
      </div>
    );
  }

  // Redirect non-authenticated users to the sign-in page
  return <Navigate to="/signin" />;
}

export default AdminProtectedRoute;
