// src/components/SignInPage.js

import React from 'react';
import { GoogleOAuthProvider, GoogleLogin } from '@react-oauth/google';
import { useNavigate } from 'react-router-dom';

const SignInPage = () => {
  const navigate = useNavigate();
  
  const handleLoginSuccess = (credentialResponse) => {
    console.log('Google Login Success:', credentialResponse);
    navigate('/users');
  };

  const handleLoginError = () => {
    console.log('Google Login Failed');
  };

  return (
    <div className="min-h-screen flex items-center justify-center bg-gradient-to-r from-blue-500 via-indigo-600 to-purple-700">
      <div className="bg-white p-8 rounded-lg shadow-lg max-w-sm w-full">
        <h2 className="text-3xl font-semibold text-center text-gray-800 mb-6">Sign In</h2>
        
        {/* Google OAuth Provider wrapping GoogleLogin */}
        <GoogleOAuthProvider clientId="YOUR_GOOGLE_CLIENT_ID_HERE">
          <GoogleLogin
            onSuccess={handleLoginSuccess}
            onError={handleLoginError}
            useOneTap
            theme="outline"
            size="large"
            className="w-full bg-blue-600 text-white py-3 px-6 rounded-md hover:bg-blue-700 focus:outline-none focus:ring-2 focus:ring-blue-500 focus:ring-opacity-50 transition duration-300"
          />
        </GoogleOAuthProvider>
        
        <div className="mt-4 text-center">
          <p className="text-sm text-gray-600">
            By signing in, you agree to our 
            <a href="#" className="text-blue-500 hover:underline"> Terms of Service</a> and
            <a href="#" className="text-blue-500 hover:underline"> Privacy Policy</a>.
          </p>
        </div>
      </div>
    </div>
  );
};

export default SignInPage;
