import { Switch, Route, Redirect } from "react-router-dom";
import React, { useState, useEffect } from 'react';
import Home from "./pages/Home";
import Tables from "./pages/Tables";
import Profile from "./pages/Profile";
import SignUp from "./pages/SignUp";
import SignIn from "./pages/SignIn.js";
import Main from "./components/layout/Main";
import "antd/dist/antd.css";
import "./assets/styles/main.css";
import "./assets/styles/responsive.css";
import PrivateRoute from './components/PrivateRoute'; // Import the PrivateRoute component
import {
  createUser, getUserByEmail, deleteUser, getAllUsers,
  createPaper, getPaperById, getAllPapers,
  uploadFile, tagPaper
} from './Api.js';

function App() {
  const [users, setUsers] = useState([]);
  const [papers, setPapers] = useState([]);
  const [file, setFile] = useState(null);
  const [email, setEmail] = useState('');
  const [userName, setUserName] = useState('');
  const [DOI, setDOI] = useState('');
  const [isAuthenticated, setIsAuthenticated] = useState(false);

  // Check if user is authenticated on load
  useEffect(() => {
    const authToken = localStorage.getItem('authToken');
    setIsAuthenticated(!!authToken); // Correctly set authentication status based on token existence

    if (authToken) {
      // Fetch all users and papers if authenticated
      getAllUsers().then(response => setUsers(response.data)).catch(console.error);
      getAllPapers().then(response => setPapers(response.data)).catch(console.error);
    }
  }, []);

  // Handle sign-in (example)
  const handleSignIn = (authToken) => {

    setIsAuthenticated(true);  // Update authentication status
    localStorage.setItem('authToken', true);  // Store token after sign-in
    console.log("User signed in, isAuthenticated set to true.");
  };

  // Handle sign-out (example)
  const handleSignOut = () => {
    localStorage.removeItem('authToken');  // Remove token
    setIsAuthenticated(false);  // Update authentication status
  };

  // Create a new user
  const handleCreateUser = () => {
    createUser({ email, name: userName })
        .then(response => setUsers([...users, response.data]))
        .catch(console.error);
  };

  // Upload a file
  const handleFileUpload = () => {
    if (!file) return;
    uploadFile(file)
        .then(response => alert('File processed:', response.data))
        .catch(console.error);
  };

  // Create a paper
  const handleCreatePaper = (userId) => {
    const paperData = {
      author: "Vamsi Inturi and GR Sabareesh and K Supradeepan and PK Penumakala",
      title: "Integrated condition monitoring scheme for bearing fault diagnosis of a wind turbine gearbox",
      year: "2019",
      journal: "Journal of Vibration and Control",
      volume: "25",
      pages: "1852-1865",
      DOI: "10.1177/1077546319841495"
    };

    createPaper(userId, paperData)
        .then(response => setPapers([...papers, response.data]))
        .catch(console.error);
  };
  console.log("Is Authenticated:", isAuthenticated);

  return (
    <div className="App">
      <Main>
        <Switch>
          <Route path="/sign-up" component={SignUp} />
          <Route path="/sign-in" render={(props) => <SignIn {...props} onSignIn={handleSignIn} />} />
          <PrivateRoute exact path="/home" component={Home} isAuthenticated={isAuthenticated} />
          <PrivateRoute exact path="/tables" component={Tables} isAuthenticated={isAuthenticated} />
          <PrivateRoute exact path="/profile" component={Profile} isAuthenticated={isAuthenticated} />
          <Redirect from="*" to={isAuthenticated ? "/home" : "/sign-in"} />
        </Switch>
      </Main>
    </div>
  );
}

export default App;
