import React, { useState } from 'react';
import axios from 'axios';

const CreatePaper = ({ userId }) => {
  const [paperDetails, setPaperDetails] = useState({
    title: '',
    author: '',
    DOI: '',
    publisher: '',
    year: '',
    journal: ''
  });
  const [message, setMessage] = useState('');

  const handleChange = (e) => {
    const { name, value } = e.target;
    setPaperDetails({
      ...paperDetails,
      [name]: value
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post(`http://localhost:3000/${userId}/paper`, paperDetails);
      setMessage('Paper created successfully!');
      setPaperDetails({
        title: '',
        author: '',
        DOI: '',
        publisher: '',
        year: '',
        journal: ''
      });
    } catch (error) {
      setMessage('Error creating paper: ' + error.response.data.message);
    }
  };

  return (
    <div>
      <h2>Create Paper</h2>
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          name="title"
          placeholder="Title"
          value={paperDetails.title}
          onChange={handleChange}
          required
        />
        <input
          type="text"
          name="author"
          placeholder="Author"
          value={paperDetails.author}
          onChange={handleChange}
          required
        />
        <input
          type="text"
          name="DOI"
          placeholder="DOI"
          value={paperDetails.DOI}
          onChange={handleChange}
          required
        />
        <input
          type="text"
          name="publisher"
          placeholder="Publisher"
          value={paperDetails.publisher}
          onChange={handleChange}
          required
        />
        <input
          type="number"
          name="year"
          placeholder="Year"
          value={paperDetails.year}
          onChange={handleChange}
          required
        />
        <input
          type="text"
          name="journal"
          placeholder="Journal"
          value={paperDetails.journal}
          onChange={handleChange}
          required
        />
        <button type="submit">Create Paper</button>
      </form>
      {message && <p>{message}</p>}
    </div>
  );
};

export default CreatePaper;
