// src/components/HomePage.js
import { useAuth } from '../context/AuthContext';
import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

const Home = () => {
  const { logout } = useAuth();
  const navigate = useNavigate();
  const [isMenuOpen, setIsMenuOpen] = useState(false);

  const toggleMenu = () => {
    setIsMenuOpen(!isMenuOpen);
  };

  const handleLogout = () => {
    logout();
  };

  const navPapers = () => {
    navigate('/user/papers');
  };

  const navCreatePaper = () => {
    navigate('/create/paper');
  };

  const navTags = () => {
    navigate('/user/taggedpapers');
  };

  return (
    <div className="min-h-screen bg-gray-100 relative">
      {/* Header */}
      <header className="bg-white shadow-md py-4 px-6 flex justify-between items-center">
        <h1 className="text-2xl font-bold text-gray-700">Welcome User</h1>
        <button
          onClick={toggleMenu}
          className="text-gray-700 text-3xl focus:outline-none"
        >
          &#9776;
        </button>
      </header>

      {/* Side Menu */}
      <aside
        className={`fixed top-0 left-0 h-full bg-white shadow-lg w-64 transform ${
          isMenuOpen ? 'translate-x-0' : '-translate-x-full'
        } transition-transform duration-300 ease-in-out`}
      >
        <div className="p-6">
          <h2 className="text-xl font-semibold text-gray-800 mb-6">Menu</h2>
          <nav className="space-y-4">
            <button
              className="w-full text-left py-2 px-4 bg-green-500 text-white font-semibold rounded-lg shadow-md hover:bg-green-600 focus:outline-none"
              type="button"
              onClick={navPapers}
            >
              View Your Citations
            </button>

            <button
              className="w-full text-left py-2 px-4 bg-cyan-500 text-white font-semibold rounded-lg shadow-md hover:bg-cyan-600 focus:outline-none"
              type="button"
              onClick={navTags}
            >
              View Your Tagged Citations
            </button>

            <button
              className="w-full text-left py-2 px-4 bg-yellow-500 text-white font-semibold rounded-lg shadow-md hover:bg-yellow-600 focus:outline-none"
              type="button"
              onClick={navCreatePaper}
            >
              Upload New Citation
            </button>

            <button
              className="w-full text-left py-2 px-4 bg-red-500 text-white font-semibold rounded-lg shadow-md hover:bg-red-600 focus:outline-none"
              type="button"
              onClick={handleLogout}
            >
              Logout
            </button>
          </nav>
        </div>
      </aside>

      {/* Main Content */}
      <main
        className={`p-6 transform ${
          isMenuOpen ? 'ml-64' : 'ml-0'
        } transition-all duration-300 ease-in-out`}
      >
        <div className="text-center">
          <h2 className="text-xl font-semibold text-gray-700">
            Select an Option from the Menu
          </h2>
        </div>
      </main>
    </div>
  );
};

export default Home;
