// src/App.js
import React from 'react';
import { BrowserRouter as Router, Route, Routes } from 'react-router-dom';
import CreatePaperWithUser from './components/CreatePaperWithUser';
import ViewUsers from './components/ViewUsers';
import ViewPapers from './components/ViewPapers';
import SignIn from './components/SignInPage';
import UserPapers from './components/UserPapers';
import { AuthProvider } from './context/AuthContext'; // Ensure to import AuthProvider
import ProtectedRoute from './components/ProtectedRoute'; // Import the ProtectedRoute component

function App() {
  return (
    <AuthProvider>  {/* Wrap the entire application in the AuthProvider */}
      <Router>
        <div className="min-h-screen bg-gray-100 flex flex-col justify-center items-center">
          <Routes>
            <Route path="/signin" element={<SignIn />} />

            {/* Protected Routes */}
            <Route
              path="/create/paper"
              element={
                <ProtectedRoute>
                  <CreatePaperWithUser />
                </ProtectedRoute>
              }
            />
            <Route
              path="/users"
              element={
                <ProtectedRoute>
                  <ViewUsers />
                </ProtectedRoute>
              }
            />
            <Route
              path="/papers"
              element={
                <ProtectedRoute>
                  <ViewPapers />
                </ProtectedRoute>
              }
            />
            <Route
              path="/user/papers"
              element={
                <ProtectedRoute>
                  <UserPapers />
                </ProtectedRoute>
              }
            />
          </Routes>
        </div>
      </Router>
    </AuthProvider>
  );
}

export default App;
