import React, { useState, useEffect } from 'react';
import { FaBars } from 'react-icons/fa';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

const Home = () => {
  const [isSidebarOpen, setIsSidebarOpen] = useState(false);
  const userId = localStorage.getItem('id');
  const [user, setUser] = useState([]);

  useEffect(() => {
    axios.get('http://localhost:3000/user/byid/:id')
        .then(response => {
            setUser(response.data); // Set the fetched users to state
        })
        .catch(error => {
            console.error('Error fetching user:', error);
        });
}, []);

  const toggleSidebar = () => {
    setIsSidebarOpen(!isSidebarOpen);
  };

  return (
    <div className="flex h-screen w-screen bg-gray-100">
      {/* Sidebar */}
      <div
        className={`fixed top-0 left-0 h-full w-64 bg-gray-800 text-white transition-transform ${
          isSidebarOpen ? 'translate-x-0' : '-translate-x-full'
        }`}
      >
        <h2 className="text-xl font-bold p-4">Menu</h2>
        <ul>
          <li className="p-4 hover:bg-gray-700">Dashboard</li>
          <li className="p-4 hover:bg-gray-700">Profile</li>
          <li className="p-4 hover:bg-gray-700">Settings</li>
          <li className="p-4 hover:bg-gray-700">Logout</li>
        </ul>
      </div>

      {/* Content */}
      <div className="flex-1 flex flex-col items-center justify-center px-4 relative">
        {/* Hamburger menu */}
        <button
          className="text-2xl absolute top-4 left-4 text-gray-700 hover:text-gray-900 focus:outline-none"
          onClick={toggleSidebar}
        >
          <FaBars />
        </button>
        <br></br>
        <br></br>
        <br></br>
        <br></br>
        <div className="bg-white p-6 rounded-lg shadow-lg w-full max-w-4xl">
          <h2 className="text-2xl md:text-3xl font-bold text-gray-800 mb-6 text-center">
            My Profile
          </h2>

          <div className="flex flex-wrap -mx-2">
            {/* Left Column */}
            <div className="overflow-x-auto bg-white shadow-lg rounded-lg">
                    <table className="min-w-full table-auto text-gray-800">
                        <thead className="bg-indigo-600 text-white">
                            <tr>
                                <th className="py-3 px-6 text-left">Name</th>
                                <th className="py-3 px-6 text-left">Email</th>
                                <th className="py-3 px-6 text-left">Actions</th>
                                
                            </tr>
                        </thead>
                        <tbody>
                            {users.map(user => (
                                <tr key={user._id} className="hover:bg-gray-50 transition duration-300 ease-in-out" onClick={() => handleRowClick(user)}>
                                    <td className="py-3 px-6">{user.name}</td>
                                    <td className="py-3 px-6">{user.email}</td>
                                    <td className="py-3 px-6 text-center">
                                        <button
                                            onClick={() => handleDelete(user._id)}
                                            className="bg-red-500 text-white py-2 px-4 rounded-md hover:bg-red-600 transition duration-300 ease-in-out"
                                        >
                                            Delete
                                        </button>
                                    </td>
                                </tr>
                            ))}
                        </tbody>
                    </table>
                </div>

            {/* Right Column */}
            <div className="w-full md:w-1/2 px-2 mb-4">
              <div className="flex items-center justify-center mb-6">
                <div className="relative">
                  <img
                    src="default-avatar.png"
                    alt="Profile"
                    className="w-24 h-24 md:w-32 md:h-32 rounded-full border border-gray-300"
                  />
                </div>
              </div>

              <label className="block text-gray-700 mt-4">Date of Birth</label>
              <div className="flex space-x-2">
                <select className="w-1/3 p-2 border border-gray-300 rounded">
                  <option>31</option>
                </select>
                <select className="w-1/3 p-2 border border-gray-300 rounded">
                  <option>September</option>
                </select>
                <select className="w-1/3 p-2 border border-gray-300 rounded">
                  <option>1990</option>
                </select>
              </div>
            </div>
          </div>

          {/* Save Button */}
          <div className="flex justify-end mt-6">
            <button className="bg-green-500 text-white font-semibold py-2 px-4 rounded hover:bg-green-600">
              Save
            </button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Home;
