// src/components/UserPapersPage.js

import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import { usePaper } from '../context/PaperContext';

const UserPapersPage = () => {
  const [papers, setPapers] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const userId = localStorage.getItem('id');
  const navigator = useNavigate();
  const {PaperId, pid} = usePaper();

  const goback = () => {
    navigator('/');
  } 

  const tagUsers = (id) => {
    pid(id);
  }

  useEffect(() => {
    // Fetch all papers for the user
    const fetchPapers = async () => {
      try {
        const response = await axios.get(`http://localhost:3000/user/${userId}/papers`);
        setPapers(response.data);
      } catch (err) {
        setError("No papers");
      } finally {
        setLoading(false);
      }
    };
    fetchPapers();
  }, [userId]);

  if (loading) return <p>Loading papers...</p>;
  if (error) return <p>{error}</p>;

  return (
    <div className="p-4 bg-blue-400">
      <button
          className="w-half py-3 px-6 bg-red-600 text-white font-semibold rounded-lg shadow-md hover:bg-red-700 focus:outline-none focus:ring-2 focus:ring-red-500 focus:ring-opacity-50 align-right"
          type="button"
          onClick={ () => goback() }
      >
          Back
      </button>
      <br></br>
      <br></br>
      <h1 className="text-3xl font-semibold mb-6">Your Published Papers</h1>
      {papers.length > 0 ? (
        <div className="space-y-4">
          {papers.map((paper) => (
            <div key={paper._id} className="p-4 bg-gray-100 rounded-lg shadow-md">
              <h2 className="text-xl font-bold">{paper.title}</h2>
              <p>Author(s): {paper.author}</p>
              <p>DOI: {paper.DOI}</p>
              <p>Publisher: {paper.publisher}</p>
              <p>Year: {paper.year}</p>
              <p>Journal: {paper.journal}</p>
              <p>Volume: {paper.volume}</p>
              <p>Pages: {paper.pages}</p>
              <br></br>
              <button
                className="w-half py-2 px-4 bg-blue-500 text-white font-semibold rounded-lg shadow-md hover:bg-blue-600 focus:outline-none"
                type="button"
                onClick={() => tagUsers(paper._id)}
              >
                Tag
              </button>
            </div>
          ))}
        </div>
      ) : (
        <p>No papers published yet.</p>
      )}
    </div>
  );
};

export default UserPapersPage;
